	/**
	 * Tests if node is valid
	 *
	 * @param      sfNestedComment $node	Propel object for src node
	 * @return     bool
	 */
	public static function isValid(sfNestedComment $node = null)
	{
		if (is_object($node) && $node->getRightValue() > $node->getLeftValue()) {
			return true;
		} else {
			return false;
		}
	}
	
