	/**
	 * Adds $delta to all L and R values that are >= $first and <= $last.
	 * '$delta' can also be negative.
	 *
	 * @param      int $delta		Value to be shifted by, can be negative
	 * @param      int $first		First node to be shifted
	 * @param      int $last			Last node to be shifted (optional)
	 * @param      int $scope		Scope to use for the shift
	 * @param      PropelPDO $con		Connection to use.
	 */
	public static function shiftRLValues($delta, $first, $last = null, $scope = null, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(sfNestedCommentPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
	
		// Shift left column values
		$whereCriteria = new Criteria(sfNestedCommentPeer::DATABASE_NAME);
		$criterion = $whereCriteria->getNewCriterion(sfNestedCommentPeer::LEFT_COL, $first, Criteria::GREATER_EQUAL);
		if (null !== $last) {
			$criterion->addAnd($whereCriteria->getNewCriterion(sfNestedCommentPeer::LEFT_COL, $last, Criteria::LESS_EQUAL));
		}
		$whereCriteria->add($criterion);
		$whereCriteria->add(sfNestedCommentPeer::SCOPE_COL, $scope, Criteria::EQUAL);
		
		$valuesCriteria = new Criteria(sfNestedCommentPeer::DATABASE_NAME);
		$valuesCriteria->add(sfNestedCommentPeer::LEFT_COL, array('raw' => sfNestedCommentPeer::LEFT_COL . ' + ?', 'value' => $delta), Criteria::CUSTOM_EQUAL);
	
		BasePeer::doUpdate($whereCriteria, $valuesCriteria, $con);
	
		// Shift right column values
		$whereCriteria = new Criteria(sfNestedCommentPeer::DATABASE_NAME);
		$criterion = $whereCriteria->getNewCriterion(sfNestedCommentPeer::RIGHT_COL, $first, Criteria::GREATER_EQUAL);
		if (null !== $last) {
			$criterion->addAnd($whereCriteria->getNewCriterion(sfNestedCommentPeer::RIGHT_COL, $last, Criteria::LESS_EQUAL));
		}
		$whereCriteria->add($criterion);
		$whereCriteria->add(sfNestedCommentPeer::SCOPE_COL, $scope, Criteria::EQUAL);
	
		$valuesCriteria = new Criteria(sfNestedCommentPeer::DATABASE_NAME);
		$valuesCriteria->add(sfNestedCommentPeer::RIGHT_COL, array('raw' => sfNestedCommentPeer::RIGHT_COL . ' + ?', 'value' => $delta), Criteria::CUSTOM_EQUAL);
	
		BasePeer::doUpdate($whereCriteria, $valuesCriteria, $con);
	}
	
	/**
	 * Adds $delta to level for nodes having left value >= $first and right value <= $last.
	 * '$delta' can also be negative.
	 *
	 * @param      int $delta		Value to be shifted by, can be negative
	 * @param      int $first		First node to be shifted
	 * @param      int $last			Last node to be shifted
	 * @param      int $scope		Scope to use for the shift
	 * @param      PropelPDO $con		Connection to use.
	 */
	public static function shiftLevel($delta, $first, $last, $scope = null, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(sfNestedCommentPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
	
		$whereCriteria = new Criteria(sfNestedCommentPeer::DATABASE_NAME);
		$whereCriteria->add(sfNestedCommentPeer::LEFT_COL, $first, Criteria::GREATER_EQUAL);
		$whereCriteria->add(sfNestedCommentPeer::RIGHT_COL, $last, Criteria::LESS_EQUAL);
		$whereCriteria->add(sfNestedCommentPeer::SCOPE_COL, $scope, Criteria::EQUAL);
		
		$valuesCriteria = new Criteria(sfNestedCommentPeer::DATABASE_NAME);
		$valuesCriteria->add(sfNestedCommentPeer::LEVEL_COL, array('raw' => sfNestedCommentPeer::LEVEL_COL . ' + ?', 'value' => $delta), Criteria::CUSTOM_EQUAL);
	
		BasePeer::doUpdate($whereCriteria, $valuesCriteria, $con);
	}
	
	/**
	 * Reload all already loaded nodes to sync them with updated db
	 *
	 * @param      PropelPDO $con		Connection to use.
	 */
	public static function updateLoadedNodes(PropelPDO $con = null)
	{
		if (Propel::isInstancePoolingEnabled()) {
			$keys = array();
			foreach (sfNestedCommentPeer::$instances as $obj) {
				$keys[] = $obj->getPrimaryKey();
			}
	
			if (!empty($keys)) {
				// We don't need to alter the object instance pool; we're just modifying these ones
				// already in the pool.
				$criteria = new Criteria(sfNestedCommentPeer::DATABASE_NAME);
				$criteria->add(sfNestedCommentPeer::ID, $keys, Criteria::IN);
	
				$stmt = sfNestedCommentPeer::doSelectStmt($criteria, $con);
				while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
					$key = sfNestedCommentPeer::getPrimaryKeyHashFromRow($row, 0);
					if (null !== ($object = sfNestedCommentPeer::getInstanceFromPool($key))) {
						$object->setLeftValue($row[13]);
						$object->setRightValue($row[14]);
						$object->setLevel($row[15]);
						$object->clearNestedSetChildren();
					}
				}
				$stmt->closeCursor();
			}
		}
	}
	
	/**
	 * Update the tree to allow insertion of a leaf at the specified position
	 *
	 * @param      int $left	left column value
	 * @param      integer $scope	scope column value
	 * @param      PropelPDO $con	Connection to use.
	 */
	public static function makeRoomForLeaf($left, $scope, PropelPDO $con = null)
	{	
		// Update database nodes
		sfNestedCommentPeer::shiftRLValues(2, $left, null, $scope, $con);
	
		// Update all loaded nodes
		sfNestedCommentPeer::updateLoadedNodes($con);
	}
	
	/**
	 * Update the tree to allow insertion of a leaf at the specified position
	 *
	 * @param      integer $scope	scope column value
	 * @param      PropelPDO $con	Connection to use.
	 */
	public static function fixLevels($scope, PropelPDO $con = null)
	{
		$c = new Criteria();
		$c->add(sfNestedCommentPeer::SCOPE_COL, $scope, Criteria::EQUAL);
		$c->addAscendingOrderByColumn(sfNestedCommentPeer::LEFT_COL);
		$stmt = sfNestedCommentPeer::doSelectStmt($c, $con);
		
		// set the class once to avoid overhead in the loop
		$cls = sfNestedCommentPeer::getOMClass(false);
		$level = null;
		// iterate over the statement
		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
	
			// hydrate object
			$key = sfNestedCommentPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null === ($obj = sfNestedCommentPeer::getInstanceFromPool($key))) {
				$obj = new $cls();
				$obj->hydrate($row);
				sfNestedCommentPeer::addInstanceToPool($obj, $key);
			}
			
			// compute level
			// Algorithm shamelessly stolen from sfPropelActAsNestedSetBehaviorPlugin
			// Probably authored by Tristan Rivoallan
			if ($level === null) {
				$level = 0;
				$i = 0;
				$prev = array($obj->getRightValue());
			} else {
				while ($obj->getRightValue() > $prev[$i]) {
					$i--;
				}
				$level = ++$i;
				$prev[$i] = $obj->getRightValue();
			}
			
			// update level in node if necessary
			if ($obj->getLevel() !== $level) {
				$obj->setLevel($level);
				$obj->save($con);
			}
		}
		$stmt->closeCursor();
	}

	// symfony behavior
	
	/**
	 * Returns an array of arrays that contain columns in each unique index.
	 *
	 * @return array
	 */
	static public function getUniqueColumnNames()
	{
	  return array();
	}

	// symfony_behaviors behavior
	
	/**
	 * Returns the name of the hook to call from inside the supplied method.
	 *
	 * @param string $method The calling method
	 *
	 * @return string A hook name for {@link sfMixer}
	 *
	 * @throws LogicException If the method name is not recognized
	 */
	static private function getMixerPreSelectHook($method)
	{
	  if (preg_match('/^do(Select|Count)(Join(All(Except)?)?|Stmt)?/', $method, $match))
	  {
	    return sprintf('BasesfNestedCommentPeer:%s:%1$s', 'Count' == $match[1] ? 'doCount' : $match[0]);
	  }
	
	  throw new LogicException(sprintf('Unrecognized function "%s"', $method));
	}

} // BasesfNestedCommentPeer

